import React, {useState} from 'react';
import {
    Steps,
    Row,
    Col,
    Image,
    Card,
    Form,
} from 'antd';
import {useTranslation} from 'react-i18next';
import FormStepper from './FormStepper';
import AddAccount from './AddAccount/AddAccount';
import AddAnimal from './AddAnimal/AddAnimal';
import AddPetPal from './AddPetPal/AddPetPal';

const { Step } = Steps;


function LandingPage() {
    const totalSteps = 2; // 0-based count, i.e. 2 -> 3
    const { t } = useTranslation();
    const [currentStep, setCurrentStep] = useState(0);
    const [accountForm] = Form.useForm();
    const [animalsForm] = Form.useForm();

    const previousStep = () => {
        setCurrentStep((prevVal => prevVal - 1))
    }
    const nextStep = () => {
        setCurrentStep((prevVal => prevVal + 1))
    }

    return (
        <Row justify='center' align='middle' style={{minHeight: '97vh'}}>
            <Col xl={{span: 4}} lg={{span: 7}} sm={{span: 6}} md={{span: 6}} style={{textAlign: 'center'}}>
                <h1>Welcome to Petpal!</h1>
                <Image
                    className='filter-green'
                    width={200}
                    height={200}
                    src="/assets/images/paw-print.svg"
                    fallback="data:image/png;base64,"
                />
            </Col>
            <Col xl={{span: 10}} lg={{span: 18}} sm={{span: 24}} md={{span: 24}}>
                <Card style={{backgroundColor: '#fffbfa'}}>
                    <Steps current={currentStep}>
                        <Step title={t('newAccountStep1.title')} description={t('newAccountStep1.description')} />
                        <Step title={t('newAccountStep2.title')} description={t('newAccountStep2.description')} />
                        <Step title={t('newAccountStep3.title')} description={t('newAccountStep3.description')} />
                    </Steps>

                    {currentStep === 0 && (
                        <>
                            <AddAccount form={accountForm} moveToNextStep={nextStep} />
                            <FormStepper currentStep={currentStep} totalSteps={totalSteps} previousStep={previousStep} form={accountForm} />
                        </>
                    )

                    }
                    {currentStep === 1 &&
                        <>
                            <AddAnimal form={animalsForm} moveToNextStep={nextStep} />
                            <FormStepper currentStep={currentStep} totalSteps={totalSteps} previousStep={previousStep} form={animalsForm} />
                        </>
                    }
                    {currentStep === 2 &&
                        <>
                            <AddPetPal />
                            <FormStepper currentStep={currentStep} totalSteps={totalSteps} previousStep={previousStep} form={animalsForm} />
                        </>

                    }
                </Card>
            </Col>
        </Row>

    );
}

export default LandingPage;
